import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, Subscription } from 'rxjs';
import { Note } from 'src/app/models/note.model';
import { NotesService } from 'src/app/services/notes.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent {

  constructor(private notesService: NotesService, private route: ActivatedRoute) { }

  note$: Observable<Note>;
  paramSubscription: Subscription;
  id: string;
  temp$: Observable<any>;

  updateNote(title: string, desc: string, id: string) {
    this.temp$ = this.notesService.updateNote(title, desc, id)
    this.paramSubscription = this.temp$.subscribe()
  }

  ngOnInit() {
    this.paramSubscription = this.route.paramMap.subscribe(value => {
      this.id = value.get('id');
      this.note$ = this.notesService.getNote(value.get('id'))
    })
  }

  ngOnDestroy() {
    this.paramSubscription.unsubscribe()
  }
}
