import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class FactService {

  constructor(private http: HttpClient) { }

  getFact() {
    return this.http.get('https://api.api-ninjas.com/v1/facts?limit=1');
  }
}
