import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { Note } from 'src/app/models/note.model';
import { NotesService } from 'src/app/services/notes.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent {

  constructor(private notesService: NotesService, private route: ActivatedRoute) { }

  note: Observable<Note> = new Observable();
  addNote(title: string, desc: string) {
    this.notesService.insertNote(title, desc)
  }

  ngOnInit() {
    this.route.paramMap.subscribe(value => {
      console.log(value.get('id'))
      this.note = this.notesService.getNote(value.get('id'))
    })
  }

  ngOnDestroy() {

  }
}
